import React, { useEffect, useState } from 'react';
import NavBar from './NavBar';
import SideBar from './Sidebar';
import ContentHeader from './ContentHeader';
import Footer from './Footer';

import axios from 'axios';
import Swal from 'sweetalert2';
import config from '../config';
import { useNavigate } from 'react-router-dom';

function BackOffice(props) {

    const navigate = useNavigate();

    const [adminName, setAdminName] = useState();
    const [adminLevel, setAdminLevel] = useState();

    useEffect(() => {
        fetchData();
    }, []);

    const fetchData = async () => {
        try {
            const _config = {
                headers: {
                    'Authorization': 'Bearer ' + localStorage.getItem('lotto_token')
                }
            }

            await axios.get(config.api_path + '/api/admin/validateLogin', _config)
                .then(res => {
                    console.log(res.data);
                    setAdminName(res.data.result.admin_name);
                    setAdminLevel(res.data.result.admin_level);
                })
                .catch(err => {
                    throw err.response.data;
                });
        } catch (e) {
            if (e.message === 'auth fail') navigate('/');

            Swal.fire({
                title: 'Error',
                text: e.message,
                icon: 'error'
            });
        }
    }

    return (
        <div className='wrapper'>
                <NavBar adminName={adminName} adminLevel={adminLevel} />
                <SideBar adminName={adminName} adminLevel={adminLevel} />

                <div className='content-wrapper'>
                    <ContentHeader headerTitle={props.headerTitle} />
                    { props.children }
                </div>

                <Footer />
            </div>
    )
}

export default BackOffice;
