import React, { useState } from 'react';
import axios from 'axios';
import config from '../config';
import Swal from 'sweetalert2';
import { useNavigate } from 'react-router-dom';

function Login() {
    const navigate = useNavigate();

    const [username, setUsername] = useState();
    const [password, setPassword] = useState();

    const handleLogin = async () => {
        try {
            await axios.post(config.api_path + '/api/admin/login', {
                username: username,
                password: password
            }).then(res => {
                console.log(res);
                if (res.data.message === 'success') {
                    if (res.data.token !== '') {
                        localStorage.setItem('lotto_token', res.data.token);
                        navigate('/home');
                    }
                } else {
                    Swal({
                        title: 'ตรวจสอบการเข้าระบบ',
                        text: 'username หรือ password ไม่ถูกต้อง',
                        icon: 'error'
                    })
                }
            }).catch(err => {
                throw err.respnse.data;
            });
        } catch (err) {
            Swal.fire({
                title: 'Error',
                text: err.message,
                icon: 'error'
            });
        }
    }

    document.body.className = 'hold-transition login-page';

    return (
        <>
            <div className="login-box">
                <div className="login-logo">
                    <b>BackOffice</b> for Admin
                </div>
                <div className="login-box-body">
                    <p className="login-box-msg">Login</p>

                    <form onSubmit={handleLogin}>
                        <div className="form-group has-feedback">
                            <label>Username</label>
                            <input onChange={e => setUsername(e.target.value)} type="text" className="form-control" />
                            <span className="glyphicon glyphicon-envelope form-control-feedback"></span>
                        </div>
                        <div className="form-group has-feedback">
                            <label>Password</label>
                            <input onChange={e => setPassword(e.target.value)} type="password" className="form-control" />
                            <span className="glyphicon glyphicon-lock form-control-feedback"></span>
                        </div>
                        <div className="row">
                            <div className="col-xs-4">
                                <button onClick={handleLogin} type="button" className="btn btn-primary btn-block btn-flat">Sign In</button>
                            </div>
                        </div>
                    </form>

                </div>
            </div>
        </>
    );
}

export default Login;
