import React, { useState } from 'react';

function Login() {
    const [username, setUsername] = useState();
    const [password, setPassword] = useState();

    const handleLogin = async () => {
        console.log(username, password);
    }

    document.body.className = 'hold-transition login-page';

    return (
        <>
            <div className="login-box">
                <div className="login-logo">
                    <b>Lotto</b> Back Office
                </div>
                <div className="login-box-body">
                    <p className="login-box-msg">Login</p>

                    <form onSubmit={handleLogin}>
                        <div className="form-group has-feedback">
                            <label>Username</label>
                            <input onChange={e => setUsername(e.target.value)} type="text" className="form-control" placeholder="Username" />
                            <span className="glyphicon glyphicon-envelope form-control-feedback"></span>
                        </div>
                        <div className="form-group has-feedback">
                            <label>Password</label>
                            <input onChange={e => setPassword(e.target.value)} type="password" className="form-control" placeholder="Password" />
                            <span className="glyphicon glyphicon-lock form-control-feedback"></span>
                        </div>
                        <div className="row">
                            <div className="col-xs-4">
                                <button onClick={handleLogin} type="button" className="btn btn-primary btn-block btn-flat">Sign In</button>
                            </div>
                        </div>
                    </form>

                </div>
            </div>
        </>
    );
}

export default Login;
